/*
 * @Author: Li yli2935@uwo.ca
 * @Date: 2023-06-09 11:28:10
 * @LastEditors: Li yli2935@uwo.ca
 * @LastEditTime: 2023-06-24 14:50:23
 * @FilePath: /practie/practice/src/modules/pages/Term.js
 * @Description: 这是默认设置,请设置`customMade`, 打开koroFileHeader查看配置 进行设置: https://github.com/OBKoro1/koro1FileHeader/wiki/%E9%85%8D%E7%BD%AE
 */
import * as React from 'react';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import Markdown from '../components/Markdown';
import Typography from '../components/Typography';
import withRoot from '../withRoot';
import {Prism as SyntaxHighlighter} from 'react-syntax-highlighter';
import {materialDark as CodeStyle} from 'react-syntax-highlighter/dist/esm/styles/prism';

const CodeBlock = ({className, children}) => {
  let lang = 'text'; // default monospaced text
  if (className && className.startsWith('lang-')) {
    lang = className.replace('lang-', '');
  }
  return (
    <SyntaxHighlighter language={lang} style={CodeStyle}>
      {children}
    </SyntaxHighlighter>
  );
}

// markdown-to-jsx uses <pre><code/></pre> for code blocks.
const PreBlock = ({children, ...rest}) => {
  if ('type' in children && children ['type'] === 'code') {
    return CodeBlock(children['props']);
  }
  return <pre {...rest}>{children}</pre>;
};


function Codes(props) {
  const { path,title } = props;
  const [post, setPost] = React.useState('');

  React.useEffect(() => {

    import('' + path)
      .then(res => {
        fetch(res.default)
          .then(res => res.text())
          .then(res => setPost(res))
          .catch(err => console.log(err));
      })
      .catch(err => console.log(err));
  });
  return (
    <React.Fragment>
      <Container>
        <Box sx={{ mt: 7, mb: 12 }}>
          <Typography variant="h5" gutterBottom marked="center" align="center">
            {title}
          </Typography>
          <br></br>

            <Markdown>
              {/* <SyntaxHighlight> */}
                {post}
              {/* </SyntaxHighlight> */}
            </Markdown>




        </Box>

        <Box align="center" marginBottom={10}>

      </Box>

      </Container>

    </React.Fragment>
  );
}

export default withRoot(Codes);
